class App extends React.Component{
    state = {
        score: 0,
        images: [],
        images_clicked: [],
        searchterm: "",
    }
    clickme = (id) => {
        // this.setState?
           if (this.state.images_clicked.indexOf(id)===-1){

            this.setState({
                   score: this.state.score + 1,
                   images_clicked: [...this.state.images_clicked, id ],
                   images:  this.state.images.sort(()=> Math.random() - 0.5),


               })
            } else { 
            this.setState({
                score:0,
                images_clicked: [],
                images: this.state.images.sort(()=> Math.random() - 0.5),
            })

        }
    }
    searchGiphy = (event) => {
        let value = event.target.value.trim();
        this.setState({
            searchterm: value
        });
        API.search(value)
    }
    render(){
    return (
        <div>
            <input type="text" value={this.state.searchterm} onChange={this.searchGiphy} />
            <div>Score: {this.state.score}</div>
            {this.state.images.map(img => (
            <Image id={img.id} alt={img.alt} src={img.src} key={img.id} clickme={this.clickme} />
            ))}
        </div>
    )
            }
}


export default App;
